function create_parameter(varobject)%	To create mpt.Parameter variables.% mpt.Parameter (handle)%             Value: 0%           RTWInfo: [1x1 mpt.CustomRTWInfoParameter]%       Description: ''%          DataType: 'auto'%               Min: -Inf%               Max: Inf%          DocUnits: ''%        Complexity: 'real'%        Dimensions: [1 1]%          EnumType: ''%     FunctionGroup: ''%            Format: ''if isfield(varobject, 'Calibratable')&&(strcmp(varobject.Calibratable, 'off'))    assignin('base', varobject.Name, varobject.DefaultValue);    return;endtry                                         sigobj = mpt.Parameter;              catch    error('Data object class ''mpt.Parameter'' has not been defined.');    return;endif ~isempty(varobject.Min)sigobj.Min = varobject.Min;endif ~isempty(varobject.Max)sigobj.Max = varobject.Max;endif strncmp(varobject.DataType, 'Enum:', 5)    sigobj.Value = eval(varobject.DefaultValue);else    sigobj.Value = ConvertViaType(varobject.DefaultValue,varobject.DataType);end% /* Change comment:DataType must be assign after the Value, Min and Max.*/ % /* For some Value, Min or Max have the type infomation!!! take 0,1 for */% /* example, matlab may thought it was logical                          */sigobj.DataType = varobject.DataType; sigobj.DocUnits = varobject.Unit;sigobj.Description = varobject.Description;sigobj.RTWInfo.StorageClass = 'Custom';sigobj.RTWInfo.CustomStorageClass = 'ASAP2';sigobj.RTWInfo.CustomAttributes.MemorySection='MEM_CALDAT';sigobj.RTWInfo.CustomAttributes.DataInit = 'Auto';sigobj.EnumType = varobject.EnumType;sigobj.FunctionGroup = varobject.Group;sigobj.Format = varobject.Format;sigobj.RTWInfo.CustomAttributes.HeaderFile = 'ASAP2Cal.h';sigobj.RTWInfo.CustomAttributes.DefinitionFile = 'ASAP2Cal.c';assignin('base', varobject.Name, sigobj);end